insert into products (id, name, description, price, category_id, created_at, updated_at) 
values
	-- Видеокарты
	(1, "PowerColor AMD Radeon RX 550", "Видеокарта PowerColor AMD Radeon RX 550 Red Dragon [AXRX 550 4GBD5-DH] с 4096 МБ видеопамяти GDDR5 позиционируется производителем как компонент для геймерского системного блока, поддерживающего запуск видеоигр со сложным графическим контентом. Подключаемое к разъему PCI-E 3.0 устройство с возможностью эффективной обработки графики обладает длиной 210 мм и демонстрирует энергопотребление не выше 50 Вт. Такая особенность позволяет успешно применять его в компактной сборке с невысокой мощностью БП.", 15499, 1, now(), now()),
	(2, "KFA2 GeForce GTX 1050 Ti", "Видеокарта KFA2 GeForce GTX 1050 Ti 1-Click OC [50IQH8DSQ31K] – отличный выбор для пользователей, которые хотят создать игровую систему начального или среднего уровня. Модель обладает привлекательным дизайном. Стоит отметить и незначительную (196 мм) длину устройства, благодаря которой у вас появится возможность использования корпуса, не отличающегося крупными размерами. Плюсом является и невысокий уровень энергопотребления: величина этого показателя равна лишь 75 Вт. Совместно с видеокартой можно использовать блок питания с мощностью от 300 Вт.", 19499, 1, now(), now()),
	(3, "GIGABYTE GeForce GTX 1650 D6 OC", "Видеокарта GIGABYTE GeForce GTX 1650 D6 OC (rev. 2.0) [GV-N1656OC-4GD rev2.0] подходит для установки в компактный системный блок. А ее технические характеристики обеспечивают реалистичную графику в большинстве игр. Она оборудована видеопроцессором Turing, который поддерживает передовые спецэффекты. За счет чего каждая игра будет увлекательной и интенсивной. Видеопамять формата GDDR6 и объемом 4 ГБ позволит воспроизводить изображения в высоком разрешении (8K UHD (Ultra HD)).", 26499, 1, now(), now()),
	(4, "Palit GeForce GTX 1660 DUAL", "Видеокарта Palit GeForce GTX 1660 DUAL [NE51660018J9-1161C] предназначена для использования в мощных компьютерных системах, при этом обладает компактными размерами. Графический процессор на архитектуре Turing работает с тактовой частотой 1530 МГц, повышаемой до показателя 1785 МГц в режиме вычислительного разгона.", 39999, 1, now(), now()),
	(5, "ASUS GeForce GTX 1660 SUPER TUF GAMING", "Видеокарта ASUS GeForce GTX 1660 SUPER TUF GAMING OC рассчитана на игровые системы. В ее основе лежит архитектура Turing, которая обеспечивает увеличение производительности до 6 раз. Видеокарта предусматривает усиление алюминиевой пластиной задней части корпуса, что придает ему структурную жесткость, защищая составляющие и предотвращая изгиб платы.", 54999, 1, now(), now()),
	(6, "GIGABYTE GeForce RTX 3060 GAMING OC", "Видеокарта GIGABYTE GeForce RTX 3060 GAMING OC Rev2.0 способна гарантировать возможность комфортного использования большинства видеоигр. Главный компонент видеоадаптера – видеопроцессор GeForce RTX 3060. Устройство не располагает поддержкой мультипроцессорной конфигурации.", 58999, 1, now(), now()),
	(7, "MSI GeForce RTX 3060 Ti GAMING X", "Самое свежее воплощение культовой серии Gaming от MSI – это все то же сочетание высокой скорости, эффективного охлаждения и безупречной эстетики, которое давно полюбилось увлеченным геймерам. Такая видеокарта позволит запускать любые игры, оставаясь холодной и тихой – именно так, как вам хочется.", 73999, 1, now(), now()),
	(8, "Inno3D GeForce RTX 3080 iChill X4", "Видеокарта INNO3D GeForce RTX 3080 iChill X4 (LHR) обеспечивает рекордную производительность для геймеров, работая на базе Ampere — архитектуры NVIDIA RTX второго поколения. Она оснащена улучшенными ядрами RT и тензорными ядрами, потоковыми мультипроцессорами и высокоскоростной памятью G6X для потрясающих игровых возможностей.", 129999, 1, now(), now()),
	(9, "MSI AMD Radeon RX 6900 XT GAMING X TRIO", "Видеокарта MSI AMD Radeon RX 6900 XT на базе архитектуры AMD RDNA 2, оснащенная 80 мощными улучшенными вычислительными блоками, кэш-памятью AMD Infinity Cache объемом 128 МБ и выделенной памятью GDDR6 объемом до 16 ГБ, разработана для обеспечения сверхвысокой частоты кадров и игрового процесса с разрешением 4K на серьезном уровне.", 149999, 1, now(), now()),
	(10, "ColorFul iGame GeForce RTX 3080 Ti Advanced OC-V", "Видеокарта ColorFul iGame GeForce RTX 3080 Ti Advanced OC-V создана на базе архитектуры Ampere — архитектуры NVIDIA RTX второго поколения. Она оснащена улучшенными ядрами для трассировки лучей и тензорными ядрами, потоковыми мультипроцессорами и высокоскоростной памятью G6 и обеспечивает необходимую мощность даже для самых требовательных игр.", 152999, 1, now(), now()),
	-- Процессоры
	(11, "AMD Ryzen 5 1600 OEM", "Шестиядерный процессор AMD Ryzen 5 1600 OEM порадует вас не только высоким уровнем быстродействия, но и безупречной надежностью. Модель рассчитана на работу в составе производительной системы. Базовая частота процессора равна 3200 МГц. Максимальная частота в турборежиме – 3600 МГц. Быстродействие модели помогает обеспечить значительный объем кеш-памяти L3 – 16 МБ. Объем кеш-памяти второго уровня равен 3 МБ.", 9750, 2, now(), now()),
	(12, "AMD Ryzen 5 2600 OEM", "Процессор AMD Ryzen 5 2600 OEM обладает лучшей в своем классе производительностью, которая удовлетворит потребности геймеров и разработчиков. Он наделен шестью ядрами, обрабатывающими запросы с частотой 3400 МГц в базовом и 3900 МГц в турборежиме. Максимально возможное число потоков – 12. Кэш третьего уровня равен 16 МБ. AMD Ryzen 5 2600 OEM рассчитан на работу с памятью стандарта DDR4, а максимальная частота памяти – 2933 МГц. Благодаря прогрессивной технологии энергосбережения Pure Power разработчикам удалось снизить тепловыделение до 65 Вт.", 10999, 2, now(), now()),
	(13, "Intel Core i5-10400F OEM", "Процессор Intel Core i5-10400F OEM способен проявить свою эффективность в составе игровых систем и мощных рабочих станций. Графическое ядро отсутствует: вам будет нужно позаботиться о выборе видеокарты. Базовая частота процессора равна 2900 МГц. Пиковая частота в турборежиме намного выше – 4300 МГц. Процессор обладает 12-мегабайтной кеш-памятью третьего уровня. Объем кеша L2 равен 1.5 МБ. Совместимая память – DDR4.", 11790, 2, now(), now()),
	(14, "Intel Core i5-9400F OEM", "6-ядерный процессор Intel Core i5-9400F, для установки которого используется сокет LGA1151-v2, представляет из себя надежнейшее устройство, возможностей которого достаточно для эффективной работы в составе большинства эксплуатируемых в настоящее время системных блоков. В основе модели, произведенной с использованием 14-нанометрового техпроцесса, лежит ядро Coffee Lake R. Базовая частота процессора равна 2900 МГц. В турборежиме частота более чем на 40 % выше – 4100 МГц. Эффективности устройства способствует значительный объем кэша третьего уровня: величина этого показателя равна 9 МБ. Объем кэша L2 равен 1.5 МБ.", 11999, 2, now(), now()),
	(15, "AMD Ryzen 5 3600 OEM", "6-ядерный процессор AMD Ryzen 5 3600 OEM порадует высоким уровнем производительности подавляющее большинство пользователей. Устройство будет уверенно себя чувствовать в составе мощной игровой системы. Базовая частота процессора равна 3600 МГц. Турбочастота – 4200 МГц. Важной особенностью процессора является очень большой объем кэша третьего уровня: величина этого показателя равна 32 МБ. Объем кэша L2 – 3 МБ.", 14199, 2, now(), now()),
	(16, "Intel Core i7-8700T OEM", "Процессор Intel Core i7-8700T OEM оборудован разъемом подключения LGA 1151-v2, который поддерживается большим количеством актуальных материнских плат. Если вы планируете собрать эффективный игровой системный блок или мощную домашнюю станцию, вам стоит обратить внимание на эту модель с 6 ядрами и поддержкой 12 потоков. ЦПУ имеет базовую тактовую частоту на уровне 2400 МГц и поддерживает технологию разгона Turbo Boost 2.0 для подъема тактовой частоты до уровня 4000 МГц.", 16699, 2, now(), now()),
	(17, "AMD Ryzen 5 3600X OEM", "Шестиядерный процессор AMD Ryzen 5 3600X OEM – отличный вариант для комплектации игрового компьютера. Перед вами – модель экстра-класса, возможности которой достаточны для эффективного использования игр, насыщенных высокодетализированной графикой. Базовая частота устройства – 3800 МГц. В турборежиме частота возрастает до 4400 МГц. Процессор имеет свободный множитель. Графическое ядро отсутствует. Это неудивительно: модель рассчитана на совместную работу с мощным дискретным видеоадаптером.", 16999, 2, now(), now()),
	(18, "Intel Core i5-11600 OEM", "Процессор Intel Core i5-11600 – чип со сбалансированной производительностью. Можно использовать его для создания различных сборок. При изготовлении применялся технологический процесс 14 нм. Он позволяет добиться небольшого потребления энергии и тепловыделения. Процессор выполнен с применением архитектуры Rocket Lake. Она позволила поднять производительность в сравнении с чипами прошлого поколения. Используется шесть ядер и двенадцать потоков для обработки информации.", 17999, 2, now(), now()),
	(19, "AMD Ryzen 7 2700X BOX", "Процессор AMD Ryzen 7 2700X BOX оборудован разъемом подключения AM4 и предназначен для эксплуатации в высокопроизводительном игровом системном блоке. Восьмиядерная модель с поддержкой 16 потоков имеет базовую тактовую частоту на уровне 3700 МГц и поддерживает технологию разгона Precision Boost 2, благодаря которой тактовую частоту можно довести до 4300 МГц. ЦПУ рассчитан на работу с памятью DDR4, тактовая частота которой находится в диапазоне 1600-2933 МГц.", 18499, 2, now(), now()),
	(20, "AMD Ryzen 5 5600X BOX", "Используйте лучшее оборудование для игр, 6 невероятных ядер, для тех, кто живет игрой. Этот лучший в мире процессор для настольных ПК гарантирует прекрасную производительность в играх.", 25599, 2, now(), now()),
	-- ОЗУ
	(21, "Corsair Vengeance LPX 8GB", "Оперативная память Corsair Vengeance LPX [CMK8GX4M1E3200C16] – модель от широчайшим образом известного производителя оборудования данного типа. Объем модуля равен 8 ГБ. Тип – DDR4. Память соответствует форм-фактору DIMM. Устройство совместимо почти с любыми эксплуатируемыми в настоящее время материнскими платами. Наиболее заметный эффект может быть достигнут при комплектации игрового системного блока. Память обладает высоким уровнем технических характеристик. Тактовая частота равна 3200 МГц, а пропускная способность – 25600 МБ/с. Предусмотрена поддержка Intel XMP. Тайминги модуля – 16-20-20-38.", 4099, 3, now(), now()),
	(22, "Kingston HyperX FURY Black 8GB", "Оперативная память Kingston HyperX FURY Black [HX436C17FB3/8] станет высокопроизводительным дополнением к аппаратной платформе компьютера, используемого для работы с требовательными приложениями и запуска игр. Она обладает объемом 8 ГБ и работает с частотой 3600 МГц, что обеспечивает высокую пропускную способность. Одной из особенностей Kingston HyperX FURY Black является алюминиевый радиатор, способствующий быстрому рассеиванию тепла. Из других особенностей отмечаются низкопрофильный дизайн и экономичное потребление энергии.", 4299, 3, now(), now()),
	(23, "Patriot Viper Steel 8GB", "Оперативная память Patriot Viper Steel [PVS48G320C6K] включает в себя два модуля емкостью 4 ГБ каждый и станет отличным выбором для игровых и рабочих систем начального уровня. Каждая планка выполнена в форм-факторе DIMM и соответствует стандарту DDR4. Отличительной особенностью каждой модели является рабочая частота, соответствующая 3200 МГц. Благодаря этому, а также пропускной способности PC25600 и таймингам, значение которых соответствует 16-20-20-40, обеспечивается высокая скорость обработки данных, что позволяет повысить быстродействие и производительность вашего компьютера.", 4699, 3, now(), now()),
	(24, "Micron 8GB", "Память Micron для серверов и рабочих станций, за счет использования высококачественных чипов собственного производства и технологий в области Flash памяти, отличается высокой стабильностью работы и наилучшими характеристиками отвечающими требованиям к модулям памяти и носителям информации.", 5499, 3, now(), now()),
	(25, "AMD Radeon R5 Entertainment Series 16GB‘", "Оперативная память AMD Radeon R5 Entertainment Series [R5S316G1601U2K] выпускается корпорацией AMD. В 16-гигабайтный комплект входят два 8-гигабайтных модуля. Память соответствует уже не слишком распространенному, но все еще достаточно популярному типу DDR3. Вы сможете укомплектовать игровой компьютер или производительный системный блок, эксплуатируемый дома или в офисе.", 6099, 3, now(), now()),
	(26, "Goodram 16GB‘", "Оперативная память Goodram [GR2666D464L19/16G] имеет объем 16 ГБ. Имеющий двустороннюю компоновку чипов модуль соответствует типу DDR4. Память совместима с абсолютным большинством эксплуатируемых в настоящее время системных плат. Напряжение питания стандартно: величина данного показателя составляет 1.2 В.", 6199, 3, now(), now()),
	(27, "Neo Forza ENCKE 16GB", "Оперативная память Neo Forza ENCKE [NMUD416E82-3200DC10] будет незаменима для использования в тандеме с мощным процессором в ситуациях перманентной многозадачности. Ведь эта память обладает внушительными данными, позволяющими ей ощутимо повышать быстродействие системы, даже когда речь идет о ресурсоемких играх либо о серьезной программно-вычислительной работе.", 6299, 3, now(), now()),
	(28, "A-Data XPG Gammix D10 16GB", "16-гигабайтная оперативная память A-Data XPG Gammix D10 [AX4U266638G16-DBG] имеет «агрессивный», говорящий сам за себя дизайн: перед вами игровое устройство, которое подходит для эксплуатации в интенсивном режиме.", 6499, 3, now(), now()),
	(29, "AMD Radeon R9 Gamer Series 16GB", "Оперативная память AMD Radeon R9 Gamer Series может использоваться в различных компьютерах. Она подойдет как для рабочих ПК, так и для игровых. Удается получить оперативную память с отличными характеристиками, которая увеличит производительность компьютера.", 6699, 3, now(), now()),
	(30, "Crucial [CT16G4DFRA32A] 16GB", "Память Crucial DIMM DDR4 PC25600 3200MHz Crucial [CT16G4DFRA32A] объёмом 16384Mb. Простой способ ускорить работу медленного компьютера — увеличить объем памяти. ", 7299, 3, now(), now()),
	-- Материнские платы
	(31, "ASRock A320M-DVS R4.0", "Материнская плата ASRock A320M-DVS R4.0 станет отличным выбором для пользователей, которые хотят собрать компактный компьютер, подходящий для универсального использования. Плата, соответствующая форм-фактору Micro-ATX, имеет габаритные размеры 230x201 мм. В основе модели – популярный чипсет AMD A320. ", 3599, 4, now(), now()),
	(32, "GIGABYTE GA-A320M-S2H", "Материнская плата GIGABYTE GA-A320M-S2H – популярная модель от широко известного производителя. Плата будет полезна любителям процессоров AMD, желающим создать надежный компьютер, предназначенный для универсального применения. Соответствие модели форм-фактору Micro-ATX дает возможность использования компактного корпуса.", 3950, 4, now(), now()),
	(33, "ASRock A520M-HVS", "Материнская плата ASRock A520M-HVS станет отличным выбором для поклонников процессоров AMD. Модель, оснащенная сокетом AM4, подходит для сборки компактного универсального компьютера. Плата поддерживает частоту оперативной памяти до 4733 МГц. Скоростной твердотельный накопитель можно подключить к разъему M.2. Форм-фактор платы – Micro-ATX.", 4399, 4, now(), now()),
	(34, "ASUS PRIME A520M-K", "Материнская плата ASUS PRIME A520M-K в форм-факторе Micro-ATX является высокопроизводительной основой для сборки настольного компьютера в компактном корпусе. Плата построена с использованием чипсета AMD A520, который имеет сокет AM4 и оптимизирован для работы с процессорами AMD.", 4650, 4, now(), now()),
	(35, "MSI H410M PRO-VH", "Устройства профессиональной серии PRO помогают работать еще эффективнее. В материнских платах MSI H410M PRO-VH сочетаются широкая функциональность и высокое качество изготовления – они идеально подходят для продуктивного труда.", 5350, 4, now(), now()),
	(36, "GIGABYTE H410M H", "Материнская плата GIGABYTE H410M H – устройство профессионального класса, подходящее для сборки офисной рабочей станции. Такой компьютер можно эффективно эксплуатировать и дома. Плата соответствует форм-фактору Micro-ATX. Это значит, что вы сможете задействовать отличающийся незначительными размерами корпус.", 5799, 4, now(), now()),
	(37, "GIGABYTE B450M GAMING", "Несмотря на компактность, материнская плата GIGABYTE B450M GAMING подходит для сборки игрового системного блока. Модель станет отличным выбором для пользователей, которые не испытывают необходимости в использовании мультипроцессорной конфигурации. Плата рассчитана на установку процессоров AMD AM4. Максимально возможный объем памяти равен 32 ГБ. Слотов памяти – 2.", 5799, 4, now(), now()),
	(38, "ASRock B450 STEEL LEGEND", "Обладающая эффектным внешним видом материнская плата ASRock B450 STEEL LEGEND соответствует форм-фактору Standard-ATX. Модель подходит для сборки производительного компьютера, в том числе – игрового. Повышенную производительность в играх можно обеспечить, установив два видеоадаптера, обладающих поддержкой CrossFire X. Используемые процессоры – AMD AM4.", 8050, 4, now(), now()),
	(39, "MSI MAG B460 TORPEDO", "Материнская плата MSI MAG B460 TORPEDO — функциональная платформа с широкими возможностями конфигурации. Модель соответствует формату Standard-ATX, что позволяет легко собрать производительную игровую систему или рабочую станцию. В основе используется чипсет Intel B460 и сокет LGA 1200, что позволяет устанавливать различные процессоры Intel.", 8999, 4, now(), now()),
	(40, "ASUS PRIME B560-PLUS", "Серия ASUS Prime раскрывает максимальные возможности процессоров Intel Core 10-го и 11-го поколения. Материнские платы Prime B560 сочетают в себе мощную систему питания, эффективное охлаждение и интеллектуальные функции настройки параметров с помощью интуитивного программного обеспечения и эксклюзивных функций BIOS.", 11399, 4, now(), now()),
	-- Блоки питания
	(41, "Xilence Red Wings XN052 500W", "Блок питания Xilence Red Wings XN052 500W [XP500R7] предлагается поставить в офисные или домашние компьютеры. Он собран с использованием надежных компонентов, имеет хорошие характеристики, адаптирован для применения в различных сборках.", 2250, 5, now(), now()),
	(42, "HIPER HPP-450", "Блок питания HIPER HPP-450 — надежное устройство. Оно подойдет пользователям персональных компьютеров, которым нужно установить в ПК качественный БП с мощностью 450 Вт. Такой мощности будет более чем достаточно, чтобы поставлять энергию к вашим комплектующим. Отличительной особенностью модели является качество исполнения — сделан блок из высококачественных компонентов, способных гарантировать стабильную и безопасную работу с компьютером.", 2399, 5, now(), now()),
	(43, "Zalman WATTBIT 600W", "Блок питания Zalman WATTBIT 600W [ZM600-XE], номинальная выходная мощность которого равна 600 Вт, подходит для комплектации высококлассных системных блоков, в том числе – игровых. Все компоненты вашей системы будут обеспечены электропитанием нужного качества. Можно будет задействовать потребляющие значительное количество энергии компоненты.", 2999, 5, now(), now()),
	(44, "Cougar XTC500", "Cougar XTC500 - это высококачественный блок питания с сертификатом 80Plus. Эффективный, бесшумный и долговечный, XTC500 - лучший ответ для тех, кто ищет блок питания. 120-мм бесшумный вентилятор Cougar имеет усовершенствованную раму, которая увеличивает концентрацию воздушного потока, а кривизна лопастей вентилятора настроена для снижения вибрации при любых нагрузках.", 3050, 5, now(), now()),
	(45, "AeroCool AERO WHITE 600W", "Блок питания AeroCool AERO WHITE 600W [ACPW-AR60AEC.A1] с легкостью обеспечит возможность функционирования компьютера высокого уровня. Выходная мощность источника питания составляет внушительные 600 Вт. Мощность по линии 12 В – 552 Вт. Возможности модели достаточны для питания мощного процессора, производительной видеокарты (или двух видеокарт) и множества других устройств.", 3099, 5, now(), now()),
	(46, "Chieftec ECO 500W", "Блок питания Chieftec ECO 500W [GPE-500S] характеризуется полной выходной мощностью на уровне 500 Вт и может обеспечивать энергией все компоненты системного блока домашней станции или ПК, собранного для решения рабочих задач в офисе. Размещенный в черном корпусе БП, принадлежащий к числу энергоэффективных моделей серии ECO, изготовлен в форм-факторе ATX. Он рассчитан на работу при уровне напряжения на входе 230 В.", 3299, 5, now(), now()),
	(47, "Chieftec Proton 500W", "Отличным приобретением для вашей сборки станет блок питания Chieftec Proton 500W [BDF-500S]. Этот БП производится из высококачественных компонентов с соблюдением всех возможных требований. Он получил сертификат 80 PLUS Bronze, отличается высоким КПД. Сопутствующие потери энергии у него небольшие.", 3899, 5, now(), now()),
	-- Накопители
	(48, "Toshiba P300 1TB", "Если Вы пребываете в поиске вместительного и долговечного жесткого диска, то Toshiba P300 [HDWD110UZSVA] – это именно то, что удовлетворит Ваши запросы. Представленное устройство разработано легендарной компанией Toshiba, что является гарантией непревзойденного качества и высокопроизводительности аппарата.", 2799, 6, now(), now()),
	(49, "WD Blue 500GB", "Жесткий диск WD Blue WD5000AZRZ – это 3.5-дюймовый носитель, предназначенный для установки внутрь моноблоков или настольных компьютеров, отличающийся легкостью (весит всего 450 граммов) сниженным энергопотреблением (3.3 Вт). 500-гигабайтный WD Blue WD5000AZRZ будет отличным приобретением тем, кто не хранит у себя громоздких файловых коллекций, а хочет использовать удобный накопитель для выполнения повседневных задач.", 2850, 6, now(), now()),
	(50, "Seagate SkyHawk 1TB", "3.5-дюймовый 1-терабайтный жесткий диск Seagate SkyHawk [ST1000VX005] – надежное устройство хранения данных, которое позиционируется производителем в качестве составной части оборудования для видеонаблюдения. Модель рассчитана на высокую интенсивность записи. Ничто не мешает вам использовать диск и в составе домашнего или офисного компьютера.", 4099, 6, now(), now()),
	(51, "Patriot Memory P210 128GB", "128 ГБ SSD-накопитель Patriot Memory P210 [P210S128G25] представляет собой производительное решение для вашего компьютера. С помощью данной модели вы сможете значительно сократить время загрузки приложений и операционной системы, а также повысить общее быстродействие в самых разных задачах. ", 1599, 6, now(), now()),
	(52, "Tammuz GK300 128GB", "Твердотельные накопители GK300 компании Tammuz значительно повышают скорость работы системы, обеспечивая значительно более высокую скорость запуска, загрузки и передачи данных по сравнению с механическими жесткими дисками. В них используется флэш-память TLC 3D NAND для обеспечения высокой скорости обработки повседневных задач и увеличения продуктивности работы пользователей.", 1750, 6, now(), now()),
	(53, "HP S700 Pro 128GB", "128 ГБ SSD-накопитель HP S700 Pro [2AP97AA#ABB] обеспечит высокую скорость загрузки приложений, а также всей операционной системы вашего компьютера, что позволит не только сократить необходимое время для выполнения задач, но и повысить комфорт при работе. ", 2350, 6, now(), now()),
	(54, "AMD Radeon R5 Series 240GB", "SSD-накопитель AMD Radeon R5 Series обладает солидным запасом памяти в 240 ГБ, за счет чего сможет уместить довольно большую коллекцию фильмов, сериалов, фотографий или видеозаписей. Однако главное достоинство прибора заключается в возможности повысить быстродействие компьютера. Скорость его чтения и записи информации (520 и 420 Мбайт/с соответственно) позволяет ему существенно превосходить традиционные жесткие диски.", 2599, 6, now(), now()),
	(55, "Goodram CX400 gen.2 256GB", "256 ГБ SSD-накопитель Goodram CX400 gen.2 [SSDPR-CX400-256-G2] представляет собой производительное решение, которое отлично подойдет для компьютеров любого типа. С его помощью вы сможете сократить время загрузки приложений и операционной системы, а также повысить общее быстродействие в самых разных задачах.", 2699, 6, now(), now()),
	(56, "HP S700 250GB", "SSD-накопитель HP S700 [2DP98AA#ABB] – классическое твердотельное SATA-устройство 2.5-дюймового форм-фактора. Модель подходит для модернизации или сборки компьютера, ориентированного на скоростную работу с любыми широко распространенными программами. Объем накопителя составляет 250 ГБ. Управляющее устройство – популярный контроллер Silicon Motion SM2258XT.", 3199, 6, now(), now()),
	(57, "Kingston KC2500 250GB", "SSD M.2 накопитель Kingston KC2500 [SKC2500M8/250G] – компактное внутреннее запоминающее устройство форма-фактора M.2 со скоростным интерфейсом PCI-E 3.0 x4. Оборудованный контроллером Silicon Motion и флэш-памятью 3D NAND, он ускоряет рабочие процессы как настольных компьютеров, так и ноутбуков или ультрабуков. ", 4050, 6, now(), now()),
	(58, "Seagate BarraCuda 510 250GB", "250-гигабайтный SSD M.2 накопитель Seagate BarraCuda 510 [ZP250CM3A001] выпускается одним из признанных мировых лидеров в области производства устройств хранения данных. Обмен информацией с накопителем осуществляется с использованием интерфейса PCI-E 3.0 x4. ", 4799, 6, now(), now()),
	(59, "A-Data XPG SX6000 Pro 512GB", "512 ГБ SSD M.2 накопитель A-Data XPG SX6000 Pro [ASX6000PNP-512GT-C] создан, чтобы повысить быстродействие вашего персонального компьютера или ноутбука как в базовых задач, так и при работе с различными ресурсными приложениями — графическими редакторами или играми.", 5150, 6, now(), now()),
	(60, "Crucial P2 500GB", "Неважно, чем вы занимаетесь — работаете на компьютере, играете в компьютерные игры или пользуетесь мобильным ПК на ходу — типичные скорости загрузки системы могут нарушить даже самые продуманные планы. Для вас хорошая новость: SSD-накопитель Crucial P2 обеспечивает молниеносную работу. ", 5299, 6, now(), now()),
	-- Кулеры
	(61, "DEEPCOOL Ice Blade 100", "Кулер DEEPCOOL Ice Blade 100 – модель башенного типа для ЦПУ Вашего компьютера. DEEPCOOL Ice Blade 100 совместим с процессорами Intel на сокетах LGA1156/LGA1155/LGA1150/LGA775 и процессорами AMD на сокетах FM2/FM1/AM3+/AM3/AM2+/AM2/940/939/754. Достигаемая рассеиваемая мощность на процессорах данных производителей составляет 95 Вт и 100 Вт соответственно.", 899, 7, now(), now()),
	(62, "CoolerMaster A71C", "Кулер для процессора CoolerMaster A71C [RR-A71C-18PA-R1] — решение горизонтального типа. Данная модель способна рассеивать до 95 Вт мощности, что делает ее отличным выбором для систем со средней производительностью. В основе кулера используется медное основание и алюминиевый радиатор, что позволяет эффективно собирать выделяемое тепло.", 940, 7, now(), now()),
	(63, "AeroCool Verkho 2 Dual", "Имеющий башенную конструкцию кулер для процессора AeroCool Verkho 2 Dual обладает совместимостью с большинством распространенных в настоящее время сокетов.", 1050, 7, now(), now()),
	(64, "ID-Cooling SE-903-B V2", "Кулер для процессора ID-Cooling SE-903-B V2 [ID-CPU-SE903-B-V2] имеет башенную конструкцию высотой 123 мм и благодаря своим возможностям станет отличным выбором для игровых и рабочих компьютеров. Отличительной особенностью данной модели является высокая производительность и способность рассеивать до 130 Вт мощности. Модуль также отличается универсальностью и может использоваться вместе с сокетами Intel и AMD.", 1150, 7, now(), now()),
	(65, "AARDWOLF PROXIMA 460", "AARDWOLF PROXIMA 460 - система охлаждения для центрального процессора. Кулер предназначен для отвода тепла от центрального процессора компьютера. Данная модель отличается эффективным отводом тепла при минимальном уровне шума. Это достигается за сче?т проработанного дизаи?на радиатора и специальных тихих лопастеи? кулера.", 1650, 7, now(), now()),
	(66, "PCCooler GI-X3", "Кулер для процессора PCCooler GI-X3 [CLPCC_X3] имеет башенную конструкцию. Устройство охлаждения обладает совместимостью с процессорами нескольких популярных сокетов производства как Intel, так и AMD.", 1750, 7, now(), now()),
	-- Корпуса
	(67, "Aerocool Tomahawk-A", "Соответствующий типоразмеру Midi-Tower корпус Aerocool Tomahawk-A может использоваться для сборки игрового системного блока начального и среднего уровня. Боковое окно из прочного оргстекла позволит вам любоваться используемыми комплектующими. Еще одной особенностью внешнего вида корпуса является многоцветная подсветка. Черный цвет корпуса будет гармонировать почти с любым интерьером.", 1799, 8, now(), now()),
	(68, "Deepcool Wave V2", "Корпус Deepcool Wave V2 привлекает ярким дизайном. Его изготовили из 0.5-миллиметровой стали, благодаря чему он перестал бояться механических повреждений и нагрузок. Корпус совмещается с платами micro-ATX и полностью соответствует типоразмеру mini-Tower. Благодаря этому его габариты стали компактными.", 2199, 8, now(), now()),
	(69, "GiNZZU A220", "Революционное инженерное решение по организации внутреннего пространства системного блока предоставило пользователям идеальное соотношение внешних размеров и вместительности. Это решение обеспечивает свободный доступ ко всем компонентам компьютера, создает дополнительное удобство сборки, апгрейда и последующей профилактики ПК.", 2299, 8, now(), now()),
	(70, "CoolerMaster MasterBox Lite 3", "Корпус CoolerMaster MasterBox Lite 3 - малогабаритная черная модель форм-фактора Minitower, рассчитанная на сборку производительного системного блока для офисной или домашней эксплуатации. Вы сможете установить внутрь данного корпуса материнскую плату типоразмера Micro-ATX или Mini-ITX и блок питания ATX, для размещения которого в корпусе предусмотрен верхний отсек. ", 2750, 8, now(), now()),
	(71, "Cougar Gemini M", "Корпус Cougar Gemini M [385TMB0.0002] подходит для сборки компактного, но производительного системного блока, в том числе – игрового. Модель, соответствующая типоразмеру Mini-Tower, совместима с платами форм-факторов Micro-ATX и Mini-ITX. Блока питания в комплектации нет.", 2899, 8, now(), now()),
	(72, "ZALMAN S4", "Корпус ZALMAN S4 типа Mid-Tower имеет конструкцию из стекла и металла толщиной 4 мм. Его лицевая панель изготовлена из оргстекла. Блок питания длиной до 18 см располагается в нижней части модели, а материнскую плату необходимо размещать вертикально.", 3199, 8, now(), now()),
	(73, "Aerocool Aero Engine RGB", "Корпус Aerocool Aero Engine RGB [ACCM-PV16012.11] станет отличной основой для создания производительной игровой или рабочей станции. Модель выполнена в строгом дизайне, отличительной чертой которого является расположенная на передней панели турбина охлаждения с RGB-подсветкой, а также стеклянная боковая крышка, что придает особый стиль и красоту.", 3499, 8, now(), now())
	;

